import './preTimeTable.css';
import React from 'react';
import { BrowserRouter as Router,Link } from 'react-router-dom';//BrowserRouter
import { Routes ,Route } from 'react-router-dom';
import {useNavigate} from 'react-router-dom';
import {useState} from "react";
import { useCookies } from 'react-cookie';
import { onLogin } from "./cookie.js";
import { setAuthToken } from "./utils";
import { Page, Pagebg, Title,ArticleContainer} from './components/ArticleStyle.js';
import { RemainTitle, RemainContainer, RemainList, RemainText, RemainBody } from './components/Style.js';

const PreTimeTable=()=> {

    let navigate = useNavigate();
    const [cookies, setCookie] = useCookies(['token']);
    const [error, setError] = useState(false);

    function CourseList({ list }) {

        const handleAddPreCourse = (e,courseName) =>{
            fetch('/remained_credits', {
                method: 'POST',
                headers: { 
                    'Content-Type': 'application/json' ,
                    'Authorization': `${token}`
                },
                body: JSON.stringify(formData)
            })
                .then(response => response.json())
                .then(data => {
                    setData(data);
                    console.log(data);
                    setDeptListData(data.deptList);
                    setGeneralListData(data.generalList);
                    setKernalListData(data.kernalList);
                    setOptListData(data.optList);
                    setPeListData(data.peList);
                    setReqListData(data.reqList);
                })
                .catch(error => {
                    console.error(error);
                });
        }
        return (
            <RemainList>
                <ArticleContainer>
                    {list.map((item, index) => (
                        <RemainBody key={index}>
                            <RemainText>{item.name}&nbsp;<br/><button className='courseInfo' onClick={() => handleAddPreCourse(item.name)}>課程資訊</button></RemainText>
                        </RemainBody>
                    ))}
                </ArticleContainer>
            </RemainList>
        );
    }


    function PreTimeTable() {
        const [searchTerm, setSearchTerm] = useState('');
        const [searchResults, setSearchResults] = useState([]);
        const [data, setData] = useState('');
        /*
        useEffect(() => {
            if (searchTerm) {          
              fetch(`/search?query=${searchTerm}`)
                .then((response) => response.json())
                .then((data) => {
                    setSearchResults(data);
                    console.log(data);
                })
                .catch((error) => {
                  console.error('搜索错误:', error);
                });
            } else {
              // 清除搜索结果
              setSearchResults([]);
            }
          }, [searchTerm]);*/

      return (
        
        <div className="PreTimeTable">    
            <input type="text" className='PreTimeTable_input' placeholder="輸入關鍵字" value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)}/>

        </div>
      );
    }

    return (

           
                <Routes>
                    <Route path="/" element={<PreTimeTable />} />
                </Routes>
             
    );
}

export default PreTimeTable;
