    function FoodArticle() {

        const formData = {
            studentID:  studentID,
            postId : postId,
          };

        useEffect(() => {
            if (!data) {
                fetch('/food_full_post', {
                    method: 'POST',
                    headers: {'Content-Type': 'application/json'},
                    body: JSON.stringify(formData)})
                .then(response => response.json())
                .then(data => {
                  if(data.studentID==studentID){
                    console.log("same");
                    setIsCreator(true);
                  }
                  else{
                    setIsCreator(false);
                    if(data.saved[0]=='true'){
                      console.log(data.saved[0]);
                      setIsFoodSaved(true);
                    }
                    else {
                      console.log(data.saved[0]);
                      setIsFoodSaved(false);
                    }
                  }
                  setData(data);
                  console.log(data);
                })
                .catch(error => {
                  console.error('Error:', error);
                });
            }
          }, [data]); // 添加依賴項data
    
          if (!data) {
            return <div>Loading...</div>;
          }

      return (
        <ArticleDetailPage>
            {openModal && <Modal closeModal={setOpenModal} type={"rating"} postId={postId}/>}
            {!openModal && < FoodDetailInfo/>}
        </ArticleDetailPage>
      );
    }

    return (
           
                <Routes>
                    <Route path="/" element={<FoodArticle />} />
                </Routes>
             
    );
}

export default FoodArticle;
