    setSelectedOption(selectedValue);

    if (selectedValue === 'food') {
      FoodHandler();
    }
    else if (selectedValue === 'rent') {
      RentHandler();
    }
    else if (selectedValue === 'changeClass') {
      ChangeClassHandler();
    }
    
