import './moodChatGPT.css';
import React from "react";
import back from './img/back.png';
import {Back}  from './components/Style.js';
import { Link } from 'react-router-dom';

class MoodChatGPT extends React.Component {
  state = {
    messages: [],
    newMessage: ''
  };

  handleMessageChange = (event) => {
    this.setState({ newMessage: event.target.value });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const { messages, newMessage } = this.state;
    if (newMessage.trim() !== '') {
      const updatedMessages = [...messages, newMessage];
      this.setState({ messages: updatedMessages, newMessage: '' });
    }
  };

  messageListRef = React.createRef();

  scrollToBottom = () => {
    if (this.messageListRef.current) {
      this.messageListRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  componentDidUpdate(prevProps, prevState) {
    if (prevState.messages.length !== this.state.messages.length) {
      this.scrollToBottom();
    }
  }

  render() {
    const { messages, newMessage, connected, userName, apiRoomName, inputName, messageData } = this.state;
    console.log("this.state.userName in render:", this.state.userName);
    const Message = ({ content, isSent }) => (
      <div className={`message ${isSent ? 'sent' : 'received'}`}>
        {content}
      </div>
    );

     return (
     <div className="Chatroom">
       <div className="chatroom-header">
       <Link to='/ChatRoomList'>
              <Back src={back} alt="回上一頁" />
          </Link>
         <h1>{this.state.apiRoomName}
         <span className="user-online">{userName} is online</span>
         </h1>
       </div>
       <div className="chatroom-messages" >
         <div className="message-list" >
           {/* {messageData.map((message, index) => {
               console.log("message:", message);
               return (
                 <Message
                   key={index}
                   content={message.text}
                   isSent={message.from === userName}
                 />
               );
             })}
           {messages.map((message, index) => {
               console.log("message:", message);
               return (
                 <Message
                   key={index}
                   content={message.text}
                   isSent={message.isSentByCurrentUser}
                 />
               );
             })} */}
             <div ref={this.messageListRef} />
         </div>
       </div>
       <div className="chatroom-input">
         <div className="enter">
           <input
             type="text"
             id="text"
             placeholder='輸入想說的話...'
             value={newMessage}
             onChange={this.handleMessageChange}
           />
           <button type="submit" onClick={this.sendMessage} disabled={!connected}>Send</button>
         </div>
       </div>
     </div>
     );
   }
}

export default MoodChatGPT;
