import React from 'react';

const Message = ({ content, isSent }) => {
  return (
    <div className={`message ${isSent ? 'sent' : 'received'}`}>
      {content}
    </div>
  );
};

const ChatRoom = () => {
  return (
    <div className="ChatRoom">
      <div className="message-list">
        <Message content="Hello there!" isSent={false} />
        <Message content="Hi! How are you?" isSent={true} />
        <Message content="I'm good, thanks. How about you?" isSent={false} />
        <Message content="I'm doing great too!" isSent={true} />
      </div>
      <form>
        <input type="text" placeholder="Type your message" />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default ChatRoom;
