import './favorite.css';
import { BrowserRouter as Router,Link } from 'react-router-dom';//BrowserRouter
import { Routes ,Route } from 'react-router-dom';
import bookmark from './img/bookmark.png';
import {Page, Pagebg, Title, PostArticleBtn, ArticleList, ArticleText, ArticleContainer, ArticleAuthor, ArticleBody, ArticlePostTime}  from './components/ArticleStyle.js';
import {useEffect,useState} from "react";


function Favorite() {
    const [data, setData] = useState([]);
    
    function SavedFinfo({ author, time, text }) {
      return (
        <ArticleContainer>
          <ArticleText>
            <ArticleAuthor>{author}</ArticleAuthor>
            <ArticlePostTime>{time}</ArticlePostTime>
            <ArticleBody>{text}</ArticleBody>
          </ArticleText>
        </ArticleContainer>
      );
    }

    function SavedHnfo({ author, time, text }) {
      return (
        <ArticleContainer>
          <ArticleText>
            <ArticleAuthor>{author}</ArticleAuthor>
            <ArticleBody>{text}</ArticleBody>
          </ArticleText>
        </ArticleContainer>
      );
    }
  
    useEffect(() => {
      const formData = {
        studentID: "00957025",
      };
  
      fetch('/favorites_load', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      })
        .then(response => response.json())
        .then(data => {
          // 從服務器獲取的 JSON 格式數據是一個物件，包含 food 和 rent 兩個屬性
          // 每個屬性的值都是一個物件的陣列，將這些陣列組合成一個新的陣列
          const newData = [...data.savedFood, ...data.savedHouse];
          setData(newData);
        })
        .catch(error => {
          console.error(error);
        });
    }, []);
  
    if (!data) {
      return <div>Loading...</div>;
    }
  
    return (
      <div className='Favorite'>
        <div id='div1'>
          <img src={bookmark} alt='收藏' className='favorite_icon'></img>
          <h1>我的收藏</h1>
        </div>
        <ArticleList>
          {data.map((item, index) => (
            <SaveFdinfo
              key={index}
              author={item.nickname}
              time={item.post_time}
              text={item.store}
            />
          ), (<SaveHdinfo
            key={index}
            author={item.name}
            time={item.post_time}
            text={item.title}
          />))}
        </ArticleList>
      </div>
    );
  }
  
  export default Favorite;
