import React, { useEffect } from 'react';

const MoodChat = () => {
  useEffect(() => {
    const iframe = document.createElement('iframe');
    iframe.src = '/dialogflow-messenger.html';  // 访问 public 文件夹中的 HTML 文件
    iframe.style.border = 'none';
    iframe.style.width = '100%';
    iframe.style.height = '300px';  // 调整高度适应你的需求
    document.getElementById('chat-container').appendChild(iframe);

    return () => {
      // Clean up by removing the iframe when the component unmounts
      document.getElementById('chat-container').removeChild(iframe);
    };
  }, []);

  return (
    <div id="chat-container">
      {/* Placeholder for the iframe */}
    </div>
  );
};

export default MoodChat;
