import './changeClass.css';
import React from 'react';
import {Page, Pagebg, Title, PostArticleBtn, ArticleList, ArticleText, ArticleContainer, ArticleAuthor, ArticleBody, PerChangeClassBtn}  from './components/ArticleStyle.js';
import { Routes ,Route,Link,useNavigate } from 'react-router-dom';
import {useState,useEffect} from "react";

const ChangeClass=()=> {
    let navigate = useNavigate();
    const [data, setData] = useState([]);


    function Articleinfo({ author, text }) {
        return (
          <ArticleContainer>
            <ArticleText>
                <ArticleAuthor>{author}</ArticleAuthor>
                <ArticleBody>{text}</ArticleBody>
            </ArticleText>
          </ArticleContainer>
        );
      }



    function ChangeClass() {
      const [value, setValue] = useState('');

      useEffect(() => {
        if (data.length === 0) {
          fetch("/course_change_have")
          .then(response => response.json())
          .then(data => {
            console.log(data);
            setData(data);
          })
          .catch(error => {
            console.error('Error:', error);
          });

        }
      }, [data]); // 添加依賴項data

      const handleShowClassInfoSubmit = (timeValue) => {

        setValue(timeValue);
        console.log(value);
        //const student_id = loginUser();
                       //Form submission happens here
            navigate("/changeClassList", {
              state: {
                studentID: "00957025",
                time:timeValue,
                },});
      }

      function ChangeClassBtn({ time}) {
        return (
          <PerChangeClassBtn onClick={() => handleShowClassInfoSubmit(time)}>{time}</PerChangeClassBtn>
        );
      }

      return (
        <Page>
            <Pagebg>
                <Title>換課板</Title>
                <Link to='/postArticle'>
                  <PostArticleBtn>我要發文</PostArticleBtn>
                </Link>
                <div className='PerChangeClassTable'>
                  <div>
                    {data.map(item => (
                      <ChangeClassBtn key={item.time} time={item.time}/>
                    ))}
                  </div>
                </div>
            </Pagebg>
        </Page>
      );
    }

    return (

                <Routes>
                    <Route path="/" element={<ChangeClass />} />
                </Routes>

    );
}

export default ChangeClass;
