//import './changeClass.css';
import React from 'react';
import dog from './img/dog.png';
import {ArticleDetailPage, ArticleDetailPosition, ArticleDetailAuthor, ArticleDetailTitle, ArticleDetailPostDate, ArticleDetailText, ArticleDetailSavedBtn, ArticleDetailComment, ArticleDetailPostCommentPosition, ArticleDetailCommentImg, ArticleDetailPostComment, ArticleDetailPostBtn}  from './components/ArticleDetailStyle.js';
import{Page, Pagebg, CommentList, CommentText, CommentContainer, CommentAuthor, CommentBody} from './components/CommentStyle.js';
import { Routes ,Route,useLocation } from 'react-router-dom';
import {useEffect,useState} from "react";

const FoodArticle=()=> {

    const location = useLocation();
    const [data, setData] = useState(null);
    const { studentID, postId } = location.state;

    function Commentinfo({ author, text }) {
        return (
          <CommentContainer>
            <CommentText>
                <CommentAuthor>{author}</CommentAuthor>
                <CommentBody>{text}</CommentBody>
            </CommentText>
          </CommentContainer>
        );
      }

    function ArticleTitleinfo({ author, title, post_time }) {
        return (
            <div>
                <ArticleDetailAuthor>{author}</ArticleDetailAuthor>
                <ArticleDetailTitle>{title}</ArticleDetailTitle>
                <ArticleDetailPostDate>{post_time}</ArticleDetailPostDate>
            </div>
        );
      }

      function ArticleDetailInfo({ address, rating, weekday_text, URL}) {
        return (
            <div>
                <div>地址: {address}</div>
                <div>連結: {URL}</div>
                <div>營業時間: {weekday_text}</div>
                <div>評分: {rating}</div>
            </div>
        );
      }


    function FoodArticle() {

        const formData = {
            studentID:  studentID,
            postId : postId,
          };

        useEffect(() => {
            if (!data) {
                fetch('/food_full_post', {
                    method: 'POST',
                    headers: {'Content-Type': 'application/json'},
                    body: JSON.stringify(formData)})
                .then(response => response.json())
                .then(data => setData(data))
                .catch(error => {
                  console.error('Error:', error);
                });
            }
          }, [data]); // 添加依賴項data
    
          if (!data) {
            return <div>Loading...</div>;
          }

      return (
        <ArticleDetailPage>
            <ArticleDetailPosition>
                <ArticleTitleinfo author={data.nickname} title={data.store} post_time={data.post_time}></ArticleTitleinfo>
                <hr></hr>
                <ArticleDetailInfo  address={data.address} rating={data.rating} weekday_text={data.weekday_text} URL={data.URL}  >拜託跟我換課，我請你吃雞排</ArticleDetailInfo>
                <hr></hr>
                <ArticleDetailSavedBtn>收藏</ArticleDetailSavedBtn>
                <hr></hr>
                <ArticleDetailComment>
                    {data.review.map(item => (
                      <Commentinfo author={item.p_name} text={item.p_review}></Commentinfo>
                    ))}
                </ArticleDetailComment>
            </ArticleDetailPosition>
            <ArticleDetailCommentImg src={dog}/>
            <ArticleDetailPostComment></ArticleDetailPostComment>
            <ArticleDetailPostBtn>送出</ArticleDetailPostBtn>
        </ArticleDetailPage>
      );
    }

    return (
           
                <Routes>
                    <Route path="/" element={<FoodArticle />} />
                </Routes>
             
    );
}

export default FoodArticle;
