function Favorite() {
    const [data, setData] = useState([]);
    
    function Savedinfo({ author, time, text }) {
      return (
        <ArticleContainer>
          <ArticleText>
            <ArticleAuthor>{author}</ArticleAuthor>
            <ArticlePostTime>{time}</ArticlePostTime>
            <ArticleBody>{text}</ArticleBody>
          </ArticleText>
        </ArticleContainer>
      );
    }
  
    useEffect(() => {
      const formData = {
        studentID: "00957025",
      };
  
      fetch('/favorites_load', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(formData)
      })
        .then(response => response.json())
        .then(data => {
          // 從服務器獲取的 JSON 格式數據是一個物件，包含 food 和 rent 兩個屬性
          // 每個屬性的值都是一個物件的陣列，將這些陣列組合成一個新的陣列
          const newData = [...data.food, ...data.rent];
          setData(newData);
        })
        .catch(error => {
          console.error(error);
        });
    }, []);
  
    if (!data) {
      return <div>Loading...</div>;
    }
  
    return (
      <div className='Favorite'>
        <div id='div1'>
          <img src={bookmark} alt='收藏' className='favorite_icon'></img>
          <h1>我的收藏</h1>
        </div>
        <ArticleList>
          {data.map((item, index) => (
            <Savedinfo
              key={index}
              author={item.nickname}
              time={item.post_time}
              text={item.store}
            />
          ))}
        </ArticleList>
      </div>
    );
  }
  
  export default Favorite;
