import React, { useEffect } from 'react';

// 封裝載入腳本的函式


const MoodChat = () => {
  useEffect(() => {
    const script = document.createElement('script');
    script.src =
      'https://www.gstatic.com/dialogflow-console/fast/messenger/bootstrap.js?v=1';
    script.async = true;

    script.onload = () => {
      window.dfMessengerSettings = {
        intent: 'WELCOME',
        chatTitle: 'IUA-test',
        agentId: 'c054175b-675f-49ae-93e2-5671f742914a',
        languageCode: 'en',
      };

      window.dialogflowMessenger = new window.dfMessenger.Messenger();
      window.dialogflowMessenger.render();
    };

    document.body.appendChild(script);

    return () => {
      // Clean up the script and Messenger instance when the component unmounts
      if (window.dialogflowMessenger) {
        window.dialogflowMessenger.close();
      }
      document.body.removeChild(script);
    };
  }, []);

  return <div id="dialogflow-container"></div>;
};

export default MoodChat;
