import './remain.css';
import React from 'react';
import logo from './img/IUAlogo.png';
import bee from './img/bee.PNG';
import bear from './img/bear.PNG';
import {Title}  from './components/ArticleStyle.js';
import {RemainTitle}  from './components/Style.js';
import { BrowserRouter as Router,Link } from 'react-router-dom';//BrowserRouter
import { Routes ,Route } from 'react-router-dom';
import {useEffect,useState} from "react";

const Remain=()=> {
    function Remain() {

        const [data, setData] = useState(null);
        const [selectedValue, setSelectedValue] = useState('');
        const [isRemainCredits, setIsRemainCredits] = useState(true);
        const [isDept, setIsDepts] = useState(false);
        const [isGeneral, setIsGeneral] = useState(false);
        const [isKernal, setIsKernal] = useState(false);
        const [isOpt, setIsOpt] = useState(false);
        const [isPE, setIsPE] = useState(false);
        const [isReq, setIsReq] = useState(false);

        const handleSelectChange = (event) => {
            setSelectedValue(event.target.value);
          };


        const formData = {
            studentID: "00957030",
          };
          useEffect(() => {
            if (!data) {
                fetch('/remained_credits', {
                    method: 'POST',
                    headers: {'Content-Type': 'application/json'},
                    body: JSON.stringify(formData)
                  })
                    .then(response => response.json())
                    .then(data => {
                      setData(data);
                      console.log(data);
                    })
                    .catch(error => {
                      console.error(error);
                    });
            }
          }, [data]); // 添加依賴項data

                    //Form submission happens here

