import './moodChatGPT.css';
import React from "react";
import back from './img/back.png';
import {Back}  from './components/Style.js';
import { Link } from 'react-router-dom';

class MoodChatGPT extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      messages: [],
      newMessage: '',
      isTyping: false, // Add a flag to track if the assistant is typing
    };

    this.messageContainerRef = React.createRef();
  }

  componentDidUpdate() {
    this.scrollToBottom();
  }

  scrollToBottom() {
    if (this.messageContainerRef.current) {
      this.messageContainerRef.current.scrollTop = this.messageContainerRef.current.scrollHeight;
    }
  }

  handleMessageChange = (event) => {
    this.setState({ newMessage: event.target.value });
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { messages, newMessage } = this.state;

    if (newMessage.trim() !== '') {
      const userMessage = {
        content: newMessage,
        isSent: true,
      };

      // Append the "正在輸入..." message to the user's message
      const typingMessage = {
        content: '正在輸入中...',
        isSent: false,
      };

      // Update the message list with the user's message and "正在輸入..." message
      const updatedMessages = [...messages, userMessage, typingMessage];
      this.setState({ messages: updatedMessages, newMessage: '', isTyping: true }, () => {
        this.scrollToBottom(); // Scroll to the bottom after updating state
        this.setTypingTimeout(); // Start a typing timeout
      });

      try {
        const response = await fetch('/chatGPT/chat', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(userMessage), // Send only the user's message
        });

        if (!response.ok) {
          throw new Error('Network response was not ok');
        }

        const responseData = await response.json();

        // Replace the last user message with the server's response
        const lastUserMessageIndex = updatedMessages.findIndex(
          (message) => message.isSent === true
        );
        if (lastUserMessageIndex !== -1) {
          updatedMessages.splice(lastUserMessageIndex, 1, responseData);
        }

        this.setState({ messages: updatedMessages }, () => {
          this.scrollToBottom(); // Scroll to the bottom after updating state
        });
      } catch (error) {
        console.error('There was a problem with the fetch operation:', error);
      }
    }
  };

  setTypingTimeout() {
    // Set a timeout to update isTyping to false after a delay (e.g., 2 seconds)
    setTimeout(() => {
      this.setState({ isTyping: false });
    }, 2000);
  }

  render() {
    const { messages, newMessage, isTyping } = this.state;
    
    return (
      <div className="Chatroom">
        <div className="chatroom-header">
          <Link to='/homePage'>
              <Back src={back} alt="回上一頁" />
          </Link>
          <h1>心情樹洞</h1>
        </div>
        <div className="chatroom-messages" >
          <div className="message-list" ref={this.messageContainerRef}>
            {messages.map((message, index) => (
              <div
                key={index}
                className={`message ${message.isSent ? 'sent' : 'received'}`}
                style={{ maxWidth: '70%' }}
              >
                {message.isSent ? (
                  message.content
                ) : (
                  <div className="received">
                    {isTyping ? '正在輸入中...' : 
                      (message.choices && message.choices.length > 0
                        ? message.choices[0].message.content
                        : '')}
                  </div>
                )}
              </div>
            ))}
         </div>
       </div>
       <div className="chatroom-input">
         <div className="enter">
           <input
             type="text"
             id="text"
             placeholder='輸入想說的話...'
             value={newMessage}
             onChange={this.handleMessageChange}
           />
           <button type="submit" onClick={this.handleSubmit} >Send</button>
         </div>
       </div>
      </div>
    );
  }
}

export default MoodChatGPT;
