import './generalEducaton.css';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Link } from 'react-router-dom';
import { Routes, Route } from 'react-router-dom';
import back from './img/back.png';
import { GEinfo } from './components/Style';

const GeneralEducaton = () => {
  function GeneralEducaton() {
    const [selectedField, setSelectedField] = useState('');
    const [responseData, setResponseData] = useState([]);

    const handleSelectChange = (e) => {
      setSelectedField(e.target.value);
    }

    useEffect(() => {
      if (selectedField) {
        const formData = {
          field: selectedField
        };

        fetch('/general_education', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(formData)
        })
        .then(response => response.json())
        .then(data => {
          setResponseData(data);
          console.log(data);
        })
        .catch(error => {
          console.error(error);
        });
      }
    }, [selectedField]);

    return (
      <div className='GeneralEducation'>
        <div className='g_education'>
          <div>
            <Link to='/Credit'>
              <img src={back} alt="回上一頁" className="g_backicon" />
            </Link>
          </div>
          <div className="g_title">
            <label className="titleText">通識領域</label>
          </div>
          <div className="selectDiv">
            <select
              className="selectMust"
              value={selectedField}
              onChange={handleSelectChange}
            >
              {/* ... options ... */}
            </select>
          </div>
          <div className='g_Lable'>
            {responseData.map((item) => (
              <GEinfo key={item.id}>
                {item.cname}<br />{item.cnumber}&emsp;{item.cgrade}&emsp;{item.cteacher}&emsp;{item.ccredit}學分
              </GEinfo>
            ))}
          </div>
        </div>
      </div>
    );
  }

  return (
    <Routes>
      <Route path="/" element={<GeneralEducaton />} />
    </Routes>
  );
}

export default GeneralEducaton;
