import './favorite.css';
import { BrowserRouter as Router,Link } from 'react-router-dom';//BrowserRouter
import { Routes ,Route } from 'react-router-dom';
import bookmark from './img/bookmark.png';
import {Page, Pagebg, Title, PostArticleBtn, ArticleList, ArticleText, ArticleContainer, ArticleAuthor, ArticleBody, ArticlePostTime}  from './components/ArticleStyle.js';
import {useEffect,useState} from "react";


const Favorite=()=>{

    const [data, setData] = useState(null);
    // const [openModal, setOpenModal] = useState(false);
    // let navigate = useNavigate();

    function Savedinfo({  author, time, text, postID } ) {
        return (
          <ArticleContainer>
            <ArticleText>
                <ArticleAuthor>{author}</ArticleAuthor>
                <ArticlePostTime>{time}</ArticlePostTime>
                <ArticleBody>{text}</ArticleBody>
            </ArticleText>
          </ArticleContainer>
        );
      }
    

    function Favorite(){
            const formData = {
              studentID: "00957025",
          };

      useEffect(() => {
      if (!data) {
          fetch('/favorites_load', {
              method: 'POST',
              headers: {'Content-Type': 'application/json'},
              body: JSON.stringify(formData) })
              .then(response => response.json())
              .then(data => {
                setData(data);
                console.log(data);})
              .catch(error => {
                console.error(error); });
      }
      }, [data]); // 添加依賴項data

      if (!data) {
      return <div>Loading...</div>;
      }

        return(
            <div className='Favorite'>
                <div id='div1'>
                    <img src={bookmark} alt='收藏' className='favorite_icon'></img>
                    <h1>我的收藏</h1>
                </div>
                <ArticleList>
                    {data.map(item => (
                      <Savedinfo key={item.food} author={item.name} time={item.post_time} text={item.title} postID={item.postId}></Savedinfo>
                    ))}
                </ArticleList>
            </div>
        );
    }

    return (    
        <Routes>
            <Route path="/" element={<Favorite />}/>
        </Routes>
    
    );
}

export default Favorite;
