import './ChatRoomList.css';
import React from 'react';
import { useEffect, useState, useRef,  } from "react";

const ChatRoomList = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredChatRooms, setFilteredChatRooms] = useState([]);

  const chatRooms = [
    { id: 1, name: 'Room 1' },
    { id: 2, name: 'Room 2' },
    { id: 3, name: '徵室友' },
    { id: 4, name: 'Chat Room 4' }
  ];

  var stompClient = null;
        var userName = null;
        var apiRoom = null;

        function setConnected(connected) {
            document.getElementById('conversationDiv').style.visibility = connected ? 'visible' : 'hidden';
            document.getElementById('response').innerHTML = '';
        }
        function initializeStompClient() {
            var socket = new SockJS('/chatroom');
            stompClient = Stomp.over(socket);
            stompClient.connect({ user: userName }, function (frame) {
                setConnected(true);
                console.log('Connected: ' + frame);
                apiRoom = localStorage.getItem('nowRoom');

                // 廣播
                stompClient.subscribe('/topic/'+apiRoom, function (messageOutput) {
                    showMessageOutput(JSON.parse(messageOutput.body));
                });

                // 私人
                stompClient.subscribe('/user/subscribe', function (messageOutput) {
                    showMessageOutput(JSON.parse(messageOutput.body));
                });
            });
        }
        
  const handleSearch = (event) => {
    const searchTerm = event.target.value;
    setSearchTerm(searchTerm);

    const filteredRooms = chatRooms.filter((room) =>
      room.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredChatRooms(filteredRooms);
  };

  return (
    <div className='ChatRoomList'>
      <input
        type="text"
        placeholder="Search chat rooms"
        value={searchTerm}
        onChange={handleSearch}
      />
      <ul>
      {searchTerm
          ? filteredChatRooms.map((chatRoom) => (
              <li key={chatRoom.id}>{chatRoom.name}</li>
            ))
          : chatRooms.map((chatRoom) => (
              <li key={chatRoom.id}>{chatRoom.name}</li>
            ))}
      </ul>
    </div>
  );
};

export default ChatRoomList;
