//import './changeClass.css';
import React from 'react';
import {ArticleDetailPage, ArticleDetailAuthor, ArticleDetailTitle, ArticleDetailText, ArticleDetailSavedBtn, ArticleDetailComment, ArticleDetailPostCommentPosition, ArticleDetailCommentImg, ArticleDetailPostComment}  from './components/ArticleDetailStyle.js';
import{Page, Pagebg, CommentList, CommentText, CommentContainer, CommentAuthor, CommentBody} from './components/CommentStyle.js';
import { Routes ,Route,Link } from 'react-router-dom';
import {useEffect,useState} from "react";

const RentArticle=()=> {

    const [data, setData] = useState(null);

    function Commentinfo({ author, text }) {
        return (
          <CommentContainer>
            <CommentText>
                <CommentAuthor>{author}</CommentAuthor>
                <CommentBody>{text}</CommentBody>
            </CommentText>
          </CommentContainer>
        );
      }

    function Articleinfo({ author, title }) {
        return (
            <div>
                <ArticleDetailAuthor>{author}</ArticleDetailAuthor>
                <ArticleDetailTitle>{title}</ArticleDetailTitle>
            </div>
        );
      }


    function RentArticle() {

        useEffect(() => {
            if (!data) {
              fetch('/rent_full_post')
                .then(response => response.json())
                .then(data => setData(data))
                .catch(error => {
                  console.error('Error:', error);
                });
            }
          }, [data]); // 添加依賴項data
    
          if (!data) {
            return <div>Loading...</div>;
          }

      return (
        <ArticleDetailPage>
            <Articleinfo  author={data.name} title={data.title}></Articleinfo>
        </ArticleDetailPage>
      );
    }

    return (
           
                <Routes>
                    <Route path="/" element={<RentArticle />} />
                </Routes>
             
    );
}

export default RentArticle;
