import './moodChatGPT.css';
import React from "react";
import back from './img/back.png';
import {Back}  from './components/Style.js';
import { Link } from 'react-router-dom';

class MoodChatGPT extends React.Component {
  constructor(props) {
    super(props);
    this.messageContainerRef = React.createRef();
    // ...
  }
  state = {
    messages: [],
    newMessage: ''
  };

  handleMessageChange = (event) => {
    this.setState({ newMessage: event.target.value });
  };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { messages, newMessage } = this.state;
  
    if (newMessage.trim() !== '') {
      const messageObject = {
        content: newMessage,
        isSent: true,
      };
  
      try {
        const response = await fetch('/chatGPT/chat', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(messageObject),
        });
  
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
  
        // Parse the response data as JSON
        const responseData = await response.json();
  
        // Combine the user's message and the received message into one array
        const updatedMessages = [...messages, messageObject, responseData];
        
        // Update the component's state with the updated messages
        this.setState({ messages: updatedMessages, newMessage: '' });
        console.log(updatedMessages);
      } catch (error) {
        console.error('There was a problem with the fetch operation:', error);
        // Handle the error, e.g., show an error message to the user
      }
    }
  };

  componentDidUpdate() {
    this.scrollToBottom();
  }
  
  scrollToBottom() {
    const messageContainer = this.messageContainerRef.current;
    if (messageContainer) {
      messageContainer.scrollTop = messageContainer.scrollHeight;
    }
  }

  render() {
    const { messages, newMessage } = this.state;
    
    return (
      <div className="Chatroom">
        <div className="chatroom-header">
          <Link to='/homePage'>
              <Back src={back} alt="回上一頁" />
          </Link>
          <h1>心情樹洞</h1>
        </div>
        <div className="chatroom-messages" >
          <div className="message-list" ref={this.messageContainerRef}>
            {messages.map((message, index) => (
              <div
                key={index}
                className={`message ${message.isSent ? 'sent' : 'received'}`}
                style={{ maxWidth: '70%' }}
              >
                {message.isSent ? (
                  message.content
                ) : (
                  <div className="received">
                    {message.choices && message.choices.length > 0
                      ? message.choices[0].message.content
                      : ''}
                  </div>
                )}
              </div>
            ))}
         </div>
       </div>
       <div className="chatroom-input">
         <div className="enter">
           <input
             type="text"
             id="text"
             placeholder='輸入想說的話...'
             value={newMessage}
             onChange={this.handleMessageChange}
           />
           <button type="submit" onClick={this.handleSubmit} >Send</button>
         </div>
       </div>
      </div>
    );
  }
}

export default MoodChatGPT;
