import './PostArticle.css';
import React from 'react';
import elmo from './img/elmo.png';
import conversation from './img/conversation.png';
import {Title}  from './components/ArticleStyle.js';
import {ArticleSubmitBtn, ArticleSubmitBtnPosition}  from './components/ArticleStyle.js';
import { BrowserRouter as Router,Link } from 'react-router-dom';//BrowserRouter
import { Routes ,Route, useNavigate, useLocation } from 'react-router-dom';
import {useEffect,useState} from "react";
import { loginUser } from "./cookie.js";

const ModifyPost=()=> {
    let navigate = useNavigate();
    const location = useLocation();
    const [data, setData] = useState(null);
    const { studentID, postId, fromSearch, ModifyType } = location.state;
    const [Fetchurl, setFetchurl] = useState("");
    const formData = {
        studentID:  '00957025',
        postId : postId,
      };

    function ArticleModifyFoodInput() {
    const [Ftitle, setFtitle] = useState("");
    const [Ftime, setFtime] = useState("");
    const [FtimeMonday, setFtimeMonday] = useState("");
    const [FtimeTuesday, setFtimeTuesday] = useState("");
    const [FtimeWednesday, setFtimeWednesday] = useState("");
    const [FtimeThursday, setFtimeThursday] = useState("");
    const [FtimeFriday, setFtimeFriday] = useState("");
    const [FtimeSaturday, setFtimeSaturday] = useState("");
    const [FtimeSunday, setFtimeSunday] = useState("");
    const [Faddress, setFaddress] = useState("");
    
    useEffect(() => {
        if (!data) {
            fetch(Fetchurl, {
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify(formData)})
            .then(response => response.json())
            .then(data => {
              setData(data);
            })
            .catch(error => {
              console.error('Error:', error);
            });
            const colonMonIndex = data.weekday_text[0].indexOf(":");
            const MonTime = data.weekday_text[0].slice(colonMonIndex + 1);
            const colonTueIndex = data.weekday_text[1].indexOf(":");
            const TueTime = data.weekday_text[1].slice(colonTueIndex + 1);
            const colonWesIndex = data.weekday_text[2].indexOf(":");
            const WesTime = data.weekday_text[2].slice(colonWesIndex + 1);
            const colonThrIndex = data.weekday_text[3].indexOf(":");
            const ThrTime = data.weekday_text[3].slice(colonThrIndex + 1);
            const colonFriIndex = data.weekday_text[4].indexOf(":");
            const FriTime = data.weekday_text[4].slice(colonFriIndex + 1);
            const colonSatIndex = data.weekday_text[5].indexOf(":");
            const SatTime = data.weekday_text[5].slice(colonSatIndex + 1);
            const colonSunIndex = data.weekday_text[6].indexOf(":");
            const SunTime = data.weekday_text[6].slice(colonSunIndex + 1);
            setFtitle(data.store);
            setFtimeMonday(MonTime);
            setFtimeTuesday(TueTime);
            setFtimeWednesday(WesTime);
            setFtimeThursday(ThrTime);
            setFtimeFriday(FriTime);
            setFtimeSaturday(SatTime);
            setFtimeSunday(SunTime);
            setFaddress(data.address);
        }
      }, [data]); // 添加依賴項data

    if (data) {
        if (data && data.weekday_text && data.weekday_text.length > 0){
            
        }
        
    }


        const openTime = ["星期一:"+FtimeMonday,"星期二:"+FtimeTuesday,"星期三:"+FtimeWednesday,"星期四:"+FtimeThursday,"星期五:"+FtimeFriday,"星期六:"+FtimeSaturday,"星期日:"+FtimeSunday];

        const handleFtitleChange = event => {
          setFtitle(event.target.value);
        };
        const handleFtimeChange = event => {
          setFtime(event.target.value);
        };
        const handleFtimeMondayChange = event => {
