/**
 * Store position information for a node
 */
export default class Position {
    start: {
        line: classNum;
        column: classNum;
    };
    end: {
        line: classNum;
        column: classNum;
    };
    source?: string;
    constructor(start: {
        line: classNum;
        column: classNum;
    }, end: {
        line: classNum;
        column: classNum;
    }, source: string);
}
